import { Col, Row, Button } from 'react-bootstrap'
import { FaTrash } from 'react-icons/fa'
import { useSelector, useDispatch } from 'react-redux'
import Job from './Job'

// Cart now needs to read/"write" to the store, reading the value
// of cart.content and being able to dispatch an action for removing
// an element from the cart!

const Favjob = () => {
  const fav = useSelector((state) => state.cart.content)
  const dispatch = useDispatch()

  return (
    <Row>
      <Col sm={12}>
        <ul style={{ listStyle: 'none' }}>
          {fav.map((book, i) => (
            <li key={i} className="my-4">
              <Button
                variant="danger"
                onClick={() => {
                  dispatch({
                    type: 'REMOVE_JOB_FAVJOB',
                    payload: i,
                  })
                }}
              >
                <FaTrash />
              </Button>
              <p>
              {Job.tittle}</p>
              <p>
              {Job.company_name}
              </p>
              </li>
          ))}
        </ul>
      </Col>
    </Row>
  )
}

export default Favjob
