export const SEARCH_JOBS = 'SEARCH_JOBS'


export const handleSubmit = (e) => {
    e.preventDefault();
    return async (dispatch, getState,query) => {
      console.log('Fetching the jobs from the API...')
      try {
        let baseEndpoint = await fetch(
            "https://strive-benchmark.herokuapp.com/api/jobs?search="
        )

            const response = await fetch(baseEndpoint+ query+ "&limit=20");
            if (response.ok) {
              let fetchJobs = await response.json();

              dispatch({
                type: SEARCH_JOBS,
                payload: fetchJobs, // the reducer is just being given
                // the final result, the array of books! so it cannot fail :)
              })
            } else {
              alert("Error fetching results");
            }
            } catch (error) {
            console.log(error);
          }
    }
}
