const initialState = {
    cart: {
      content: [], 
    },
  }

  const mainReducer = (state = initialState, action) => {

    switch (action.type) {


      case 'ADD_JOB_FAVJOB':
        return {
          ...state,
          cart: {
            ...state.cart,
            content: [...state.cart.content, action.payload],
          },
        }

      case 'REMOVE_JOB_FAVJOB':
        return {
          ...state,
          cart: {
            ...state.cart,
            content: state.cart.content.filter((book, i) => {
              return i !== action.payload
            }),
          },
        }

      default:
        return state
    }
  }

  export default mainReducer
